def isAnagram(string1, string2):

  # Проверьте, равны ли длины обеих строк
  if len(string1) != len(string2):
    return False

  # Словарь для хранения частотности символов в string1
  count = [0] * 26

  # Для каждого символа в string1 увеличьте его частоту
  for i in range(len(string1)):
    count[ord(string1[i]) - ord('a')] += 1

  # Для каждого символа в string2 уменьшите его частоту
  for i in range(len(string2)):
    count[ord(string2[i]) - ord('a')] -= 1

  # Если любая частота становится отрицательной,
  # то строки не могут быть анаграммами
  for i in range(len(count)):
    if count[i] < 0:
      return False

  # Если все частоты равны нулю, string1 и string2
  # являются анаграммами друг друга
  return True

